mapper.select:
select a.`code`, a.`client_id`, a.`name`, a.`ability_id`,a.`cron`, a.`command`, a.`status`, a.`execute_time` as executeTime, a.`create_time`, a.`update_time`, b.`gateway_id`,b.`name` as clientName,b.`last_link_time`,b.`product_id` as productId, c.`name` as gatewayName,c.`address`, d.`name` as abilityName,d.`property`,d.`scope`,d.`type` as abilityType from iot_appointment a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id` left join iot_ability d on a.`ability_id` = d.`id`
a.`code` = #{code}
and a.`client_id` = #{clientId}
and a.`name` like concat(#{name},"%")
and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d') and date_format(#{endTime},'%Y-%m-%d')
and a.`status` = #{status}
limit 1

select
    a.`code`, a.`client_id`, a.`name`, a.`ability_id`,a.`cron`, a.`command`, a.`status`, a.`execute_time` as executeTime, a.`create_time`, a.`update_time`,
    b.`gateway_id`,b.`name` as clientName,b.`last_link_time`,b.`product_id` as productId,
    c.`name` as gatewayName,c.`address`,
    d.`name` as abilityName,d.`property`,d.`scope`,d.`type` as abilityType
from iot_appointment a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id` left join iot_ability d on a.`ability_id` = d.`id`


        a.`code` = #{code}


        and a.`client_id` = #{clientId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`status` = #{status}


order by a.`status` asc,a.`create_time` desc
limit #{startLine},#{endLine}

select
    count(*)
from iot_appointment a


        a.`code` = #{code}


        and a.`client_id` = #{clientId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`status` = #{status}

select
    a.`code`, a.`client_id`, a.`name`, a.`ability_id`,a.`cron`, a.`command`, a.`status`, a.`create_time`, a.`update_time`,
    b.`gateway_id`,b.`name` as clientName,b.`last_link_time`,
    c.`name` as gatewayName,c.`address`
from iot_appointment a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id`


        a.`code` = #{code}


        and a.`client_id` = #{clientId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`status` = #{status}
