mapper.select:
select a.`id`, a.`gateway_id`, a.`name`, a.`serial_num`, a.`last_link_time`, a.`enable`,a.`remove`, a.`create_time`, a.`update_time`, b.`uuid`,b.`name` as gatewayName,b.`address`,b.`source` from iot_client a left join iot_gateway b on b.id = a.gateway_id
b.`source` = #{source}
and (a.`name` like concat(#{keyWord},"%") or b.`name` like concat(#{keyWord},"%"))

select
  a.`id`, a.`gateway_id`, a.`name`, a.`serial_num`, a.`last_link_time`, a.`enable`,a.`remove`, a.`create_time`, a.`update_time`,
  b.`uuid`,b.`name` as gatewayName,b.`address`,b.`source`,
  c.id as productId,c.`name` as productName
from iot_client a left join iot_gateway b on b.id = a.gateway_id
left join iot_product_type c on c.id = a.product_id


        and a.`id` = #{id}


        and a.`gateway_id` = #{gatewayId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`last_link_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`enable` = #{enable}


        and a.`remove` = #{remove}


limit 1

select
    a.`id`, a.`gateway_id`, a.`name`, a.`serial_num`, a.`last_link_time`, a.`enable`,a.`remove`, a.`create_time`, a.`update_time`,
    b.`uuid`,b.`name` as gatewayName,b.`address`,b.`source`,
    c.`id` as productId,c.`name` as productName
from iot_client a left join iot_gateway b on b.id = a.gateway_id
left join iot_product_type c on c.id = a.product_id


        and a.`id` = #{id}


        and a.`gateway_id` = #{gatewayId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`last_link_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`enable` = #{enable}


        and a.`remove` = #{remove}


order by a.`id` desc
limit #{startLine},#{endLine}

select count(*) from iot_client a


        and a.`id` = #{id}


        and a.`gateway_id` = #{gatewayId}


        and a.`name` like concat(#{name},"%")


        and date_format(a.`last_link_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and a.`enable` = #{enable}


        and a.`remove` = #{remove}
