mapper.select:
select a.`id`, a.`client_id`, a.`command`, a.`excute_time`, b.`gateway_id`,b.`name` as clientName,b.`last_link_time`, c.`name` as gatewayName,c.`address` from iot_history_execute a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id`
a.`id` = #{id}
and a.`client_id` = #{clientId}
and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d') and date_format(#{endTime},'%Y-%m-%d')
limit 1

select
a.`id`, a.`client_id`, a.`command`, a.`excute_time`,
b.`gateway_id`,b.`name` as clientName,b.`last_link_time`,
c.`name` as gatewayName,c.`address`
from iot_history_execute a left join iot_client b on a.`client_id` = b.`id` left join iot_gateway c on b.`gateway_id` = c.`id`


        a.`id` = #{id}


        and a.`client_id` = #{clientId}


        and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


order by a.`id` desc
limit #{startLine},#{endLine}

select
    count(1)
from iot_history_execute a


        a.`id` = #{id}


        and a.`client_id` = #{clientId}


        and date_format(a.`excute_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')
