mapper.select:
select a.`id`, a.`name`, a.`ability_id` as abilityId,a.`symbol`, a.`value1`,a.`value2`, a.`enable`, a.`create_time`, a.`update_time`,a.`event_attribute`,a.`event_value`, b.`product_id` as productId, b.`property`,b.`standard_id` as standardId,b.`type`,b.`scope`,b.`rw_flag` as rwFlag, c.`name` as unitName,c.`symbol` as unitSymbol from iot_warning_rules a left join iot_ability b on a.`ability_id` = b.`id` left join iot_standard_unit c on b.`standard_id` = c.`id`
a.`id` = #{id}
and a.`name` = #{name}
and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d') and date_format(#{endTime},'%Y-%m-%d')
limit 1

        select
          a.`id`, a.`name`, a.`ability_id` as abilityId,a.`symbol`, a.`value1`,a.`value2`, a.`enable`, a.`create_time`, a.`update_time`,a.`event_attribute`,a.`event_value`,
		  b.`product_id` as productId, b.`property`,b.`standard_id` as standardId,b.`type`,b.`scope`,b.`rw_flag` as rwFlag,
          c.`name` as unitName,c.`symbol` as unitSymbol
        from
        iot_warning_rules a
		left join iot_ability b on a.`ability_id` = b.`id`
		left join iot_standard_unit c on b.`standard_id` = c.`id`
        left join iot_product_rules e on `e.rule_id` = a.`id`


                a.`id` = #{id}


                and a.`name` = #{name}


                and a.`enable` = #{enable}


                and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


                and e.`product_id` = #{productId}


        order by a.`id` desc
        limit #{startLine},#{endLine}

select
    count(*)
from iot_warning_rules a
left join iot_product_rules e on `e.rule_id` = a.`id`


        a.`id` = #{id}


        and a.`name` = #{name}


        and a.`enable` = #{enable}


        and date_format(a.`create_time`,'%Y-%m-%d') between date_format(#{beginTime},'%Y-%m-%d')  and date_format(#{endTime},'%Y-%m-%d')


        and e.`product_id` = #{productId}
